import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import "./style.css";
import axios from "axios";
import Results from "../components/Results";

const books = require("google-books-search");

class Search extends Component {
  constructor(props) {
    super(props)

  this.state = {
    books: [],
    searchTitle: "",
    title: "",
    authors: "",
    description: "",
    image: "",
    link: ""
  }

  this.handleFormSubmit = this.handleFormSubmit.bind(this)
}

  componentDidMount() {
    this.loadBooks();
  }

  loadBooks = () => {
    API.getBooks()
      .then(res =>
        this.setState({ books: res.data, title: "", author: "", synopsis: "" })
      )
      .catch(err => console.log(err));
  };

  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    let currentComponent = this;
    event.preventDefault();
    books.search(this.state.searchTitle, function(error, results) {
      if (!error) {
        currentComponent.setState({
          books: [results]
        })
        console.log(results);
        console.log(this.state.books)

      } else {
        console.log(error);
      }
    })

  };  

  render() {

const ResultList = this.state.books.map(books => <Results id={books.id} />)

    return (
      <div>
        <Container fluid>
          <Row>
            <Col size="md-12">
              <Jumbotron>
                <h1>React Google Books API Search</h1>
              </Jumbotron>
            </Col>
          </Row>
        </Container>
        <Container fluid id="formContainer">
          <Row>
            <Col size="md-12">
              <form>
                <Input
                  value={this.state.searchTitle}
                  onChange={this.handleInputChange}
                  name="searchTitle"
                  placeholder="Title (required)"
                />
                <FormBtn onClick={this.handleFormSubmit}>Submit Book</FormBtn>
              </form>
            </Col>
          </Row>
          <Col size="12">
          <li className="book">

          <Container fluid>
          <Col size="12">

            {this.state.books.length ? (
              <ResultList />
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Container>



        <p className="book-author">
          by {this.state.books.author} | {this.state.books.publisher}
        </p>
        <p className="book-description">{this.state.books.description}</p>
      </li>
          </Col>
        </Container>
      </div>
    );
  }
}

export default Search;
