import axios from "axios";

export default {
  // Gets all books
  getBooks: function() {
    return axios.get("/api/books");
  },
  // Gets the book with the given id
  getBook: function(id) {
    return axios.get("/api/books/" + id);
  },
  // Deletes the book with the given id
  deleteBook: function(id) {
    return axios.delete("/api/books/" + id);
  },
  // Saves a book to the database
  saveBook: function(bookData) {
    return axios.post("/api/books", bookData);
  },
  searchBook: function(book) {
    return axios
      .get(
        "https://www.googleapis.com/books/v1/volumes?q="+ book.title + "&key=AIzaSyAX3TqovnM8kzt0QG_BD4R5gTwjLngZJPg "
      )
      .then(function(response) {
        console.log(response)
        this.setState({
          title: response.items[0].volumeInfo.title,
          authors: response.items[0].volumeInfo.authors,
          description: response.items[0].volumeInfo.description,
          image: response.items[0].volumeInfo.imageLinks.smallThumbnail,
          link:  response.items[0].volumeInfo.previewLink
        })
      })
      .catch(function(error) {
        console.log(error);
      });
  }
};
